    public function parse_order ($order, $market = null) {
        $id = $this->safe_string($order, 'id');
        $symbol = null;
        $marketId = $this->safe_string($order, 'market');
        $market = $this->safe_value($this->markets_by_id, $marketId, $market);
        if ($market !== null) {
            $symbol = $market['symbol'];
        }
        $price = $this->safe_float($order, 'price');
        $average = $this->safe_float($order, 'averagePrice');
        $amount = $this->safe_float($order, 'volume');
        $filled = $this->safe_float($order, 'executedVolume');
        $remaining = $this->safe_float($order, 'remainingVolume');
        $cost = null;
        if ($average !== null) {
            if ($filled !== null) {
                $cost = $average * $filled;
            } else {
                $cost = $average * $amount;
            }
        }
        $status = $this->parse_order_status($this->safe_string($order, 'state'));
        $side = $this->safe_string($order, 'side');
        if ($side === 'bid') {
            $side = 'buy';
        } else if ($side === 'ask') {
            $side = 'sell';
        }
        $type = $this->safe_string($order, 'orderType');
        $timestamp = $this->parse8601 ($this->safe_string($order, 'createdAt'));
        $trades = null; // not to be confused with $trades count
        return array (
            'id' => $id,
            'datetime' => $this->iso8601 ($timestamp),
            'timestamp' => $timestamp,
            'lastTradeTimestamp' => null,
            'status' => $status,
            'symbol' => $symbol,
            'type' => $type,
            'side' => $side,
            'price' => $price,
            'cost' => $cost,
            'average' => $average,
            'amount' => $amount,
            'filled' => $filled,
            'trades' => $trades,
            'remaining' => $remaining,
            'fee' => null,
            'info' => $order,
        );
    }

