        $order = array (
            'side' => $side,
            'volume' => $amount,
        );
        if ($type !== null) {
            $order['orderType'] = $type;
        }
        if ($price !== null) {
            $order['price'] = $price;
        }
        $request = array (
            'market' => $market['id'],
            'orders' => array ( $order ),
        );
        $response = $this->$method (array_merge ($request, $params));
        return $this->parse_order($response[0], $market);
    }

